import React, {useState, useEffect} from 'react';
import { Link } from "react-router-dom";
import { toast} from 'react-toastify';
import axios from 'axios';
import "./Home.css";
import "./assets/css/bootstrap.min.css";
import Container from 'react-bootstrap/Container';
import Table from 'react-bootstrap/Table';

function Home() {

    const [data, setData] = useState([]);
    const loadData = async () => {
        const response = await axios.get("http://localhost:5000/api/get");
        setData(response.data);
    };

    useEffect(()=>{
        loadData();
    }, []);

  return (
    
    <Container>
        <Table striped bordered hover responsive className="style-table">
            <thead>
                <tr>
                    <th style={{textAlign: "center"}}>SL</th>
                    <th style={{textAlign: "center"}}>Name</th>
                    <th style={{textAlign: "center"}}>Email</th>
                    <th style={{textAlign: "center"}}>Contact</th>
                    <th style={{textAlign: "center"}}>Action</th>
                </tr>
            </thead>
            <tbody>
            {data.map((row, index) => {
                return(
                  <tr key={row.id}>
                    <td scope="row" style={{textAlign: "center"}}>{index+1}</td>
                    <td style={{textAlign: "left"}}>{row.name}</td>
                    <td style={{textAlign: "left"}}>{row.email}</td>
                    <td style={{textAlign: "left"}}>{row.contact}</td>
                    <td style={{textAlign: "left"}}> Edit | Delete</td>
                  </tr>
                )
            })}
            </tbody>
        </Table>
    </Container>
  )
}

export default Home
